import { Component, h, Host } from '@stencil/core';
/**
 * @slot (default) - Content placed within the container
 */
export class AmplifyAuthContainer {
    componentWillLoad() {
        console.warn('Version `1.x` of Amplify UI has been deprecated and will be removed in a future major version of `aws-amplify`. Please visit https://ui.docs.amplify.aws/ for the current version of Amplify UI.');
    }
    render() {
        return (h(Host, null,
            h("form", { autoComplete: "on", hidden: true },
                h("input", { name: "username" }),
                h("input", { name: "password", type: "password" }),
                h("input", { type: "submit" })),
            h("slot", null)));
    }
    static get is() { return "amplify-auth-container"; }
}
